import React from "react";
import styled, { createGlobalStyle } from "styled-components";
import title from "./asset/title.png";
const GlobalStyle = createGlobalStyle`


html,
body {
	width: 100%;
	height: 100%;
}

html,
body,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
code,
blockquote,
abbr,
sub,
sup,
time,
em,
strong,
img,
q,
dl,
dt,
dd,
address,
cite,
ul,
ol,
li,
header,
section,
footer,
article,
nav,
aside,
table,
caption,
thead,
tbody,
tfoot,
tr,
td,
th,
form,
fieldset,
legend,
label {
	margin: 0;
	padding: 0;
}

ul,
ol,
li {
	list-style: none;
}
q {
	quotes: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}
textarea {
	resize: none;
}

a:link {
	color: #222;
	text-decoration: none;
}
a:visited {
	color: #222;
}
a:hover {
	color: #222;
}
a:active {
	color: #222;
}

em,
address {
	font-style: normal;
}
hr,
caption,
legend {
	display: none;
}
img,
fieldset {
	border: none;
}
label,
button {
	cursor: pointer;
}

body {
	color: #222;
	font-size: 12px;
	/* font-size:0.8em; */
	line-height: 1.2;
	font-family: 'Nanum Gothic', 돋움, 굴림;
}

.text-center {
	height: 100px;
	line-height: 100px;
	text-align: center;
}

.text-hide {
	display: none;
}

/* 스크린리더가 읽을 수 있어 접근성이 향상됨 */
.sr-only {
	position: absolute;
	left: -9999px;
}

.clearfix {
	*zoom: 1;
}

.clearfix:after {
	display: block;
	clear: both;
	content: '';
}

`;

const Header = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  min-width: 1280px;
  height: 110px;
  background-color: #fff;
  border: 1px solid black;
`;
const Language = styled.select`
  border: none;
  outline: none;
`;

const Logo = styled.img`
  display: block;
  position: absolute;
  left: 50%;
  top: 10px;
  width: 74px;
  height: auto;
  transform: translate(-50%, 0);
`;

const Nav = styled.nav`
  position: absolute;
  left: calc(50% - 150px);
  bottom: 5px;

  > span {
    margin-right: 100px;
    font-size: 13px;
    &:last-child {
      margin: 0;
    }
  }
`;

const User = styled.div`
  > i {
    margin: 20px;
  }
`;
const Menu = styled.p`
  > i {
    margin-right: 20px;
  }
`;
const Section = styled.section``;
const Footer = styled.footer``;

const Layout = () => {
  return (
    <>
      <GlobalStyle />
      <Header>
        <User>
          <i class="far fa-user"></i>
          <Language>
            <option>한국어</option>
            <option>English</option>
            <option>일본어</option>
          </Language>
        </User>
        <Logo src={title}></Logo>

        <Nav>
          <span>SHOP</span>
          <span>NEWS</span>
          <span>ABOUT</span>
        </Nav>
        <Menu>
          <i class="fas fa-search"> </i>

          <i class="fas fa-shopping-bag"></i>

          <i class="fas fa-bars"></i>
        </Menu>
      </Header>
      <Section />
      <Footer />
    </>
  );
};

export default Layout;
