import React, { useContext, useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { NavLink, Outlet, useNavigate } from 'react-router-dom';
import title from './asset/title.png';
import title2 from './asset/title2.png';
import HumanCtxprovider, { CtxState } from './context/HumanCtxprovider';
import Home from './pages/Home';
import { useEffect } from 'react';

const GlobalStyle = createGlobalStyle`


html,
body {
	width: 100%;
	height: 100%;
}

html,
body,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
code,
blockquote,
abbr,
sub,
sup,
time,
em,
strong,
img,
q,
dl,
dt,
dd,
address,
cite,
ul,
ol,
li,
header,
section,
footer,
article,
nav,
aside,
table,
caption,
thead,
tbody,
tfoot,
tr,
td,
th,
form,
fieldset,
legend,
label {
	margin: 0;
	padding: 0;
}

ul,
ol,
li {
	list-style: none;
}
q {
	quotes: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}
textarea {
	resize: none;
}

a:link {
	color: #222;
	text-decoration: none;
}
a:visited {
	color: #222;
}
a:hover {
	color: #222;
}
a:active {
	color: #222;
}

em,
address {
	font-style: normal;
}
hr,
caption,
legend {
	display: none;
}
img,
fieldset {
	border: none;
}
label,
button {
	cursor: pointer;
}

body {
	color: #222;
	font-size: 12px;
	/* font-size:0.8em; */
	line-height: 1.2;
	font-family: 'Nanum Gothic', 돋움, 굴림;
}

.text-center {
	height: 100px;
	line-height: 100px;
	text-align: center;
}

.text-hide {
	display: none;
}

/* 스크린리더가 읽을 수 있어 접근성이 향상됨 */
.sr-only {
	position: absolute;
	left: -9999px;
}

.clearfix {
	*zoom: 1;
}

.clearfix:after {
	display: block;
	clear: both;
	content: '';
}

`;

const Header = styled.header`
	display: flex;
	justify-content: space-between;
	align-items: center;
	position: relative;
	/* min-width: 1280px; */
	width: 100%;
	height: 130px;
	background-color: #fff;
`;
const Selection = styled.select`
	border: none;
	outline: none;
	&:after {
		border: 0px solid;
		border-color: black, transparent, transparent, transparent;
		content: 'dd';
	}
`;

const Logo = styled.img`
	display: block;
	position: absolute;
	left: 50%;
	top: 10px;
	width: 74px;
	height: auto;
	transform: translate(-50%, 0);
`;

const Nav = styled.nav`
	display: flex;
	position: absolute;
	left: calc(50% - 300px);
	bottom: 5px;
	> a {
		display: block;
		width: 100px;
		height: 20px;
		margin-right: 150px;
		font-size: 13px;
		text-align: center;

		&:last-child {
			margin: 0;
		}
	}
`;

const SubNav = styled.div`
	position: absolute;
	left: 50%;
	top: -9999px;
	z-index: 100;
	width: 110vw;
	height: 300px;
	background-color: #fff;
	transform: translate(-50%, 0);
	transition: 0.3s;

	&.show {
		top: 20px;
	}
`;
const LnbContainer = styled.div`
	display: flex;
	justify-content: space-around;
	position: absolute;
	left: 50%;
	top: 50%;

	transform: translate(-55%, -50%);
`;

const Lnb = styled.ul`
	width: 280px;
	> li {
		margin-bottom: 25px;
		text-align: center;
		> a {
			display: block;
			width: 100%;
			height: 100%;
		}
	}
`;

const User = styled.div`
	> i {
		margin: 20px;
		font-size: 22px;
	}
	> p {
		display: none;
		> span {
			letter-spacing: 2px;
			font-weight: bold;
			font-size: 16px;
			text-decoration: underline;
		}
		&.ment {
			display: block;
		}
	}
`;
const Menu = styled.p`
	> i {
		margin-right: 20px;
		font-size: 18px;
	}
`;

const Search = styled(SubNav)`
	height: 100px;
	&.on {
		top: 130px;
	}
	> .searchBox {
		display: flex;
		justify-content: flex-start;
		align-items: bottom;
		position: absolute;
		left: 50%;
		top: 50%;
		width: 500px;
		height: 40px;
		border-bottom: 1px solid black;
		transform: translate(-50%, -50%);
		> input {
			width: 90%;
			border: none;
			outline: none;
			padding-left: 40px;
		}
		> i {
			margin-right: 20px;
			&:last-child {
				margin: 0;
			}
		}
	}
`;

const Section = styled.section`
	position: relative;
	width: 100vw;
	min-height: 800px;
`;

const Shadow = styled.div`
	display: none;
	width: 100%;
	height: 100%;
	position: absolute;
	left: 0;
	top: 0;
	right: 0;
	bottom: 0;
	background-color: #000;
	opacity: 70%;
	&.on {
		display: block;
	}
`;

const Footer = styled.footer`
	position: relative;
	> div {
		width: 50%;
		display: block;
		position: absolute;
		left: 50%;
		background-color: #fff;
		transform: translate(-50%, 0);
	}

	> .footNav {
		top: 0;
		> div {
			/* border: none; */
			border-bottom: 1px solid #ccc;
			> ul {
				border: none;
				> li {
					text-align: left;
				}
			}
		}
	}
	> .ect {
		top: 250px;
		> div {
			/* border: none; */
			> ul {
				border: none;
				> li {
					text-align: left;
				}
			}
		}
	}
`;
const SecondLogo = styled.div`
	top: 550px;
	width: 207px;
	height: 37px;
	background-image: url(${title2});
	background-position: center center;
	background-repeat: no-repeat;
	background-size: contain;
	transform: translate(-50%, 0);
`;

const Layout = () => {
	const navigate = useNavigate();
	const state = useContext(CtxState);
	const [shop, setShop] = useState(state);

	const { show, search } = shop;

	// const { isLogin } = state;
	const storage = JSON.parse(localStorage['human'] || JSON.stringify(state));

	const shopFn = () => {
		setShop((prev) => ({ ...prev, show: !state.show }));
	};

	const shopOutFn = () => {
		setShop(!show);
	};

	const searchFn = () => {
		setShop((prev) => ({ ...prev, search: !state.search }));
	};

	const searchOutFn = () => {
		setShop(!search);
		alert(search);
	};

	const myPageFn = () => {
		navigate('/account/');
	};
	const loginFn = () => {
