import React, { useState } from 'react';
import styled from 'styled-components';

const LoginContainer = styled.div`
	display: flex;
	flex-flow: column nowrap;
	justify-content: space-around;
	position: absolute;
	left: 50%;
	top: 50%;
	p {
		color: #f00;
	}

	margin: 0 auto;
	transform: translate(-50%, -50%);
`;
const Title = styled.h2`
	margin: 0 auto;
`;

const Form = styled.form`
	display: flex;
	flex-flow: column nowrap;
	justify-content: space-around;
	height: 320px;
`;

const Label = styled.label``;
const FirstName = styled.input`
	width: 440px;
	height: 40px;
	padding-left: 10px;
	border: 1px solid #ccc;
`;

const RegisterButton = styled.button`
	width: 450px;
	height: 40px;
	margin: 10px 0 0 0;
	line-height: 40px;
	text-align: center;
	color: #fff;
	background-color: #000;
`;
const LastName = styled(FirstName)``;
const Email = styled(FirstName)``;
const Password = styled(FirstName)``;

const Register = () => {
	const [input, setInput] = useState({
		fName: '',
		lName: '',
		regiEmail: '',
		regiPsw: '',
	});

	const { fName, lName, regiEmail, regiPsw } = input;

	const changeFn = (evt) => {
		const { id, value } = evt.taget;
		setInput((prev) => ({
			...prev,
			[id]: value,
		}));
		console.log(evt.taget.value);
	};
	return (
		<>
			<LoginContainer>
				<Title>REGISTER</Title>

				<Form>
					<Label htmlFor="fName">
						<FirstName
							onChange={changeFn}
							value={fName}
							id="fName"
							placeholder="FirstName "
							autoFocus
						/>
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="lName">
						<LastName onChange={changeFn} value={lName} id="lName" placeholder="LastName " />
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="regiEmail">
						<Email onChange={changeFn} value={regiEmail} id="regiEmail" placeholder="Email " />
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="regiPsw">
						<Password onChange={changeFn} value={regiPsw} id="regiPsw" placeholder="Password " />
						<p>*잘못된 형식입니다.</p>
					</Label>
				</Form>
				<RegisterButton>CREATE MY ACCONUT</RegisterButton>
			</LoginContainer>
		</>
	);
};

export default Register;
