import React, { useState } from 'react';
import styled from 'styled-components';

const LoginContainer = styled.div`
	display: flex;
	flex-flow: column nowrap;
	justify-content: space-around;
	position: absolute;
	left: 50%;
	top: 50%;
	p {
		color: #f00;
	}

	margin: 0 auto;
	transform: translate(-50%, -50%);
`;
const Title = styled.h2`
	margin: 0 auto;
`;

const Form = styled.form`
	display: flex;
	flex-flow: column nowrap;
	justify-content: space-around;
	height: 320px;
`;

const Label = styled.label``;
const FirstName = styled.input`
	width: 440px;
	height: 40px;
	padding-left: 10px;
	border: 1px solid #ccc;
`;

const RegisterButton = styled.button`
	width: 450px;
	height: 40px;
	margin: 10px 0 0 0;
	line-height: 40px;
	text-align: center;
	color: #fff;
	background-color: #000;
`;
const LastName = styled(FirstName)``;
const Email = styled(FirstName)``;
const Password = styled(FirstName)``;

const Register = () => {
	const [input, setInput] = useState({
		fName: '',
		lName: '',
		regiEmail: '',
		regiPsw: '',
	});

	const [isInput, setIsInput] = useState({
		isFname: false,
		isLname: false,
		isRegiEmail: false,
		isRegiPsw: false,
	});

	const { isFname, isLname, isRegiEmail, isRegiPsw } = isInput;
	const { fName, lName, regiEmail, regiPsw } = input;

	const emailChangeFn = (evt) => {
		const { id, value } = evt.target;

		setInput((prev) => ({
			...prev,
			[id]: value,
		}));

		const currentEmail = value;
		// setInput(currentEmail);
		const emailRegExp =
			/^[A-Za-z0-9_]+[A-Za-z0-9]*[@]{1}[A-Za-z0-9]+[A-Za-z0-9]*[.]{1}[A-Za-z]{1,3}$/;

		if (!emailRegExp.test(currentEmail)) {
			console.log('이메일의 형식이 올바르지 않습니다!');
			setIsInput((prev) => ({
				...prev,
			}));
		} else {
			console.log('사용 가능한 이메일 입니다.');
			setIsInput((prev) => ({
				...prev,
				isRegiEmail: !prev.isRegiEmail,
			}));
		}
	};

	const isFnameChangeFn = (evt) => {
		const { id, value } = evt.target;

		setInput((prev) => ({
			...prev,
			[id]: value,
		}));

		const currentIsFname = evt.target.value;

		const isFnameRegExp = /^[a-zA-zㄱ-ㅎ|ㅏ-ㅣ|가-힣]{1,12}$/;

		if (!isFnameRegExp.test(currentIsFname)) {
			console.log('"한글 또는 영문으로 1글자 이상 12이하로 입력해주세요  "');
			setIsInput((prev) => ({
				...prev,
			}));
		} else {
			console.log('확인 되었습니다.');
			setIsInput((prev) => ({
				...prev,
				isFname: !prev.isFname,
			}));
		}
	};

	const isLnameChangeFn = (evt) => {
		const { id, value } = evt.target;

		setInput((prev) => ({
			...prev,
			[id]: value,
		}));
		const currentIsLname = evt.target.value;

		const isLnameRegExp = /^[a-zA-zㄱ-ㅎ|ㅏ-ㅣ|가-힣]{1,12}$/;

		if (!isLnameRegExp.test(currentIsLname)) {
			console.log('"한글 또는 영문으로 1글자 이상 12이하로 입력해주세요  "');
			setIsInput((prev) => ({
				...prev,
			}));
		} else {
			console.log('확인 되었습니다.');
			setIsInput((prev) => ({
				...prev,
				isLname: !prev.isLname,
			}));
		}
	};
	const regiPswChangeFn = (evt) => {
		const { id, value } = evt.target;

		setInput((prev) => ({
			...prev,
			[id]: value,
		}));

		const currentPassword = evt.target.value;

		const passwordRegExp = /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,25}$/;
		if (!passwordRegExp.test(currentPassword)) {
			console.log('"숫자+영문자+특수문자 조합으로 8자리 이상 입력해주세요!"');
			setIsInput((prev) => ({
				...prev,
			}));
		} else {
			console.log('안전한 비밀번호 입니다.');
			setIsInput((prev) => ({
				...prev,
				isRegiPsw: !prev.isRegiPsw,
			}));
		}
	};

	return (
		<>
			<LoginContainer>
				<Title>REGISTER</Title>

				<Form>
					<Label htmlFor="fName">
						<FirstName
							onChange={isFnameChangeFn}
							value={fName}
							id="fName"
							placeholder="FirstName "
							autoFocus
						/>
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="lName">
						<LastName onChange={isLnameChangeFn} value={lName} id="lName" placeholder="LastName " />
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="regiEmail">
						<Email onChange={emailChangeFn} value={regiEmail} id="regiEmail" placeholder="Email " />
						<p>*잘못된 형식입니다.</p>
					</Label>

					<Label htmlFor="regiPsw">
						<Password
							type="password"
							onChange={regiPswChangeFn}
							value={regiPsw}
							id="regiPsw"
							placeholder="Password "
						/>
						<p>*잘못된 형식입니다.</p>
					</Label>
				</Form>
				<RegisterButton>CREATE MY ACCONUT</RegisterButton>
			</LoginContainer>
		</>
	);
};

export default Register;
